mapper.select:
SELECT 
FROM chat_session 
WHERE session_id = #{sessionId} AND user_id = #{userId}

SELECT 
FROM chat_session 
WHERE user_id = #{userId} 
ORDER BY last_message_time DESC 
LIMIT #{offset}, #{size}

SELECT FROM chat_session WHERE user_id = #{userId}
AND (last_message_time < (SELECT last_message_time FROM chat_session WHERE session_id = #{cursor}) OR (last_message_time = (SELECT last_message_time FROM chat_session WHERE session_id = #{cursor}) AND session_id < #{cursor}))
ORDER BY last_message_time DESC, session_id DESC LIMIT #{size}

SELECT COUNT(*) 
FROM chat_session 
WHERE user_id = #{userId}

mapper.update:
UPDATE chat_session 
SET title = #{title}, update_time = NOW() 
WHERE session_id = #{sessionId}

UPDATE chat_session 
SET status = 0, update_time = NOW() 
WHERE session_id = #{sessionId} AND user_id = #{userId}

UPDATE chat_session 
SET message_count = message_count + 1, update_time = NOW() 
WHERE session_id = #{sessionId}

UPDATE chat_session 
SET last_message_time = NOW(), update_time = NOW() 
WHERE session_id = #{sessionId}

UPDATE chat_session 
SET status = 0, update_time = NOW() 
WHERE user_id = #{userId} AND session_id != #{sessionId}

UPDATE chat_session 
SET status = 1, update_time = NOW() 
WHERE session_id = #{sessionId} AND user_id = #{userId}

mapper.select:
SELECT 
FROM chat_session


        AND user_id = #{userId}


        AND status = #{status}


        AND title LIKE CONCAT('%', #{title}, '%')


        AND create_time >= #{startTime}


        AND create_time <= #{endTime}


ORDER BY last_message_time DESC

mapper.delete:
DELETE FROM chat_session 
WHERE status = 0 
AND last_message_time < #{expireTime}
